#include "ul32.h"


uint32_t ul32_addc(uint32_t *dst, const uint32_t *a, const uint32_t *b) {
#ifdef UL32_ADDC_64BIT
	uint64_t c = ((uint64_t)*a) + ((uint64_t)*b);
	*dst = c;
	return (c >> 32);
#else
	uint32_t r = 0;
	
	uint32_t c = (*a & 0x7fffffff) + (*b & 0x7fffffff);
	r = (*a >> 31) + (*b >> 31) + (c >> 31);
	*dst = (r << 31) | (c & 0x7fffffff);
	
	return (r >> 1);
#endif
}

uint32_t clz(uint32_t x) {
  uint32_t i = 0;
  uint32_t m = 0x80000000;
  if (0 == x)
    return 0;

  while (0 == (x & m)) {
    i++;
    m = m >> 1;
  }

  return i;
}

