#include "ul544_1.h"

/*
 * Compute the quotient with remainder of two ul544's (q = a/b_, r = a%b_, resp.)
 */
void ul544_divrem(ul544 Q_, ul544 R_, const ul544 A_in, const ul544 B_in) {
    ul576 Q; ul576_set_ui(Q, 0);
    ul544 R; ul544_set_ui(R, 0);
    if (ul544_cmp(A_in, B_in) < 0) {
        ul544_set(Q_, (struct ul544_s *)Q);
        ul544_set(R_, A_in);
        return;
    }
    
    /* Locate the most significant word of B_in           */
    int n = 17; /* B_in->x[n-1] is the MSW. */
    while ((n > 0) && (!B_in->x[n-1]))
        n--;
    /* NB: if n == 0, then B_in = 0, and we've been asked */
    /* to comptue a div by 0. Thus we assume n > 0, which */
    /* means (for instance) that n-1 is a valid index     */
    /* into B_in.                                         */
    
    /* Check to see if B_in is normalized, meaning that   */
    /* B_in->x[n-1] >= 2^31.                              */
    unsigned int k = clz(B_in->x[n-1]);
    
    /* Normalize A_in and B_in. Note that B_in's MSW is   */
    /* the same as B's (normalization does not change it) */
    /* but the same is not necessarily true of A and A_in. */
    ul576 A; ul576_set_ui(A, 0);
    A->x[17] = (0) | ((k > 0) ? (A_in->x[16] >> (32-k)) : 0);
    A->x[16] = (A_in->x[16] << k) | ((k > 0) ? (A_in->x[15] >> (32-k)) : 0);
    A->x[15] = (A_in->x[15] << k) | ((k > 0) ? (A_in->x[14] >> (32-k)) : 0);
    A->x[14] = (A_in->x[14] << k) | ((k > 0) ? (A_in->x[13] >> (32-k)) : 0);
    A->x[13] = (A_in->x[13] << k) | ((k > 0) ? (A_in->x[12] >> (32-k)) : 0);
    A->x[12] = (A_in->x[12] << k) | ((k > 0) ? (A_in->x[11] >> (32-k)) : 0);
    A->x[11] = (A_in->x[11] << k) | ((k > 0) ? (A_in->x[10] >> (32-k)) : 0);
    A->x[10] = (A_in->x[10] << k) | ((k > 0) ? (A_in->x[9] >> (32-k)) : 0);
    A->x[9] = (A_in->x[9] << k) | ((k > 0) ? (A_in->x[8] >> (32-k)) : 0);
    A->x[8] = (A_in->x[8] << k) | ((k > 0) ? (A_in->x[7] >> (32-k)) : 0);
    A->x[7] = (A_in->x[7] << k) | ((k > 0) ? (A_in->x[6] >> (32-k)) : 0);
    A->x[6] = (A_in->x[6] << k) | ((k > 0) ? (A_in->x[5] >> (32-k)) : 0);
    A->x[5] = (A_in->x[5] << k) | ((k > 0) ? (A_in->x[4] >> (32-k)) : 0);
    A->x[4] = (A_in->x[4] << k) | ((k > 0) ? (A_in->x[3] >> (32-k)) : 0);
    A->x[3] = (A_in->x[3] << k) | ((k > 0) ? (A_in->x[2] >> (32-k)) : 0);
    A->x[2] = (A_in->x[2] << k) | ((k > 0) ? (A_in->x[1] >> (32-k)) : 0);
    A->x[1] = (A_in->x[1] << k) | ((k > 0) ? (A_in->x[0] >> (32-k)) : 0);
    A->x[0] = (A_in->x[0] << k) | (0);
    
    ul576 B; ul576_set_ui(B, 0);
    B->x[17] = (0) | ((k > 0) ? (B_in->x[16] >> (32-k)) : 0);
    B->x[16] = (B_in->x[16] << k) | ((k > 0) ? (B_in->x[15] >> (32-k)) : 0);
    B->x[15] = (B_in->x[15] << k) | ((k > 0) ? (B_in->x[14] >> (32-k)) : 0);
    B->x[14] = (B_in->x[14] << k) | ((k > 0) ? (B_in->x[13] >> (32-k)) : 0);
    B->x[13] = (B_in->x[13] << k) | ((k > 0) ? (B_in->x[12] >> (32-k)) : 0);
    B->x[12] = (B_in->x[12] << k) | ((k > 0) ? (B_in->x[11] >> (32-k)) : 0);
    B->x[11] = (B_in->x[11] << k) | ((k > 0) ? (B_in->x[10] >> (32-k)) : 0);
    B->x[10] = (B_in->x[10] << k) | ((k > 0) ? (B_in->x[9] >> (32-k)) : 0);
    B->x[9] = (B_in->x[9] << k) | ((k > 0) ? (B_in->x[8] >> (32-k)) : 0);
    B->x[8] = (B_in->x[8] << k) | ((k > 0) ? (B_in->x[7] >> (32-k)) : 0);
    B->x[7] = (B_in->x[7] << k) | ((k > 0) ? (B_in->x[6] >> (32-k)) : 0);
    B->x[6] = (B_in->x[6] << k) | ((k > 0) ? (B_in->x[5] >> (32-k)) : 0);
    B->x[5] = (B_in->x[5] << k) | ((k > 0) ? (B_in->x[4] >> (32-k)) : 0);
    B->x[4] = (B_in->x[4] << k) | ((k > 0) ? (B_in->x[3] >> (32-k)) : 0);
    B->x[3] = (B_in->x[3] << k) | ((k > 0) ? (B_in->x[2] >> (32-k)) : 0);
    B->x[2] = (B_in->x[2] << k) | ((k > 0) ? (B_in->x[1] >> (32-k)) : 0);
    B->x[1] = (B_in->x[1] << k) | ((k > 0) ? (B_in->x[0] >> (32-k)) : 0);
    B->x[0] = (B_in->x[0] << k) | (0);
    
    /* Now we compute m, the difference between the MSW of */
    /* A and B.                                            */
    int m = 18 - n;
    while ((m > 0) && (!A->x[n+m-1]))
        m--;
    /* We know that m >= 0 since A_in >= B_in, which we    */
    /* determined above.                                   */
    
    /* Compute Q->x[m]: */
    {
        ul576 beta_m_B; ul576_set_ui(beta_m_B, 0);
        ul576_lshiftw(beta_m_B, B, m);
        
        if (ul576_cmp(A, beta_m_B) >= 0) {
            Q->x[m] = 1;
            ul576_sub(A, A, beta_m_B);
        }
        else
            Q->x[m] = 0;
    }
    
    /* Compute the rest of Q: */
    {
      int j = 0;
      for (j = m-1; j >= 0; j--) {
          uint64_t q_star = ( (((uint64_t)A->x[n+j]) << 32) | ((uint64_t)A->x[n+j-1]) )/((uint64_t)B->x[n-1]);
          Q->x[j] = (q_star < ((uint64_t)0x00000000ffffffff)) ? q_star : 0xffffffff;
          
          /* A <- A - q_j * beta^j * B */
          {
              ul576 qjbj_B; ul576_set_ui(qjbj_B, 0);
              ul576_lshiftw(qjbj_B, B, j);
              ul576_mulu32(qjbj_B, qjbj_B, Q->x[j]);
              ul576_sub(A, A, qjbj_B);
          }
          
          /* If A < 0, roll back Q->x[j] and adjust A: */
          while (A->x[17] & (1 << 31)) {
              Q->x[j]--;
              ul576 bj_B; ul576_set_ui(bj_B, 0);
              ul576_lshiftw(bj_B, B, j);
              ul576_add(A, A, bj_B);
          }
      }
    }
    
    /* The remainder is now the de-normalized value of A: */
    R->x[0] = ((k > 0) ? (A->x[1] << (32-k)) : 0) | (A->x[0] >> k);
    R->x[1] = ((k > 0) ? (A->x[2] << (32-k)) : 0) | (A->x[1] >> k);
    R->x[2] = ((k > 0) ? (A->x[3] << (32-k)) : 0) | (A->x[2] >> k);
    R->x[3] = ((k > 0) ? (A->x[4] << (32-k)) : 0) | (A->x[3] >> k);
    R->x[4] = ((k > 0) ? (A->x[5] << (32-k)) : 0) | (A->x[4] >> k);
    R->x[5] = ((k > 0) ? (A->x[6] << (32-k)) : 0) | (A->x[5] >> k);
    R->x[6] = ((k > 0) ? (A->x[7] << (32-k)) : 0) | (A->x[6] >> k);
    R->x[7] = ((k > 0) ? (A->x[8] << (32-k)) : 0) | (A->x[7] >> k);
    R->x[8] = ((k > 0) ? (A->x[9] << (32-k)) : 0) | (A->x[8] >> k);
    R->x[9] = ((k > 0) ? (A->x[10] << (32-k)) : 0) | (A->x[9] >> k);
    R->x[10] = ((k > 0) ? (A->x[11] << (32-k)) : 0) | (A->x[10] >> k);
    R->x[11] = ((k > 0) ? (A->x[12] << (32-k)) : 0) | (A->x[11] >> k);
    R->x[12] = ((k > 0) ? (A->x[13] << (32-k)) : 0) | (A->x[12] >> k);
    R->x[13] = ((k > 0) ? (A->x[14] << (32-k)) : 0) | (A->x[13] >> k);
    R->x[14] = ((k > 0) ? (A->x[15] << (32-k)) : 0) | (A->x[14] >> k);
    R->x[15] = ((k > 0) ? (A->x[16] << (32-k)) : 0) | (A->x[15] >> k);
    R->x[16] = ((k > 0) ? (A->x[17] << (32-k)) : 0) | (A->x[16] >> k);
    ul544_set(Q_, (struct ul544_s *)Q);
    ul544_set(R_, R);
}

int ul544_modinv(ul544 dst, const ul544 src, const ul544 n);



/*
 * Set the modulus for a mod544
 */
void mod544_set(mod544 dst, const ul544 n) {
    uint32_t tmp = 0;
    ul544_set(dst->n, n);
    tmp = 2 + n->x[0];
    tmp = tmp * (2 + n->x[0] * tmp);
    tmp = tmp * (2 + n->x[0] * tmp);
    tmp = tmp * (2 + n->x[0] * tmp);
    tmp = tmp * (2 + n->x[0] * tmp);
    dst->np = tmp;
    
    ul544 one; ul544_set_ui(one, 1);
    
    ul544_modmul(dst->rsq, one, one, dst); /* dst->rsq <- 1/r */
    ul544_modmul(dst->rsq, dst->rsq, one, dst); /* dst->rsq <- 1/rsq */
    ul544_modinv(dst->rsq, dst->rsq, dst->n); /* dst->rsq <- rsq */
}



